if(!(u.deleted&&u.created&&u.modified)){
return u
}
var n=false;
var s=u.deleted.id.split(",");
var p={};
var h=[];
for(var j=0;
j<s.length;
j++){
var w=s[j];
var g=ZmOrganizer.normalizeId(w);
if(g<0){
p[g]=true;
n=true
}else{
h.push(w)
}}
if(!n){
return u
}
var m=false;
var t=ZmRequestMgr._getObjList(u.created);
var f={};
var l={};
for(var j=0;
j<t.length;
j++){
var c=t[j];
var w=c.id;
var O=c._name;
if(O=="m"){
f[w]=c
}else{
if(O=="c"&&(c.n>1)){
l[w]=c;
m=true
}}}
if(!m){
return u
}
var x=false;
var q={};
var z=ZmRequestMgr._getObjList(u.modified);
var d={};
for(var j=0;
j<z.length;
j++){
var T=z[j];
var w=T.id;
var g=ZmOrganizer.normalizeId(w);
var O=T._name;
if(O=="m"){
var e=g*-1;
if(p[e]&&l[T.cid]){
x=true;
d[w]=T;
q[T.cid]=appCtxt.multiAccounts?ZmOrganizer.getSystemId(e):e;
l[T.cid]._wasVirtConv=true;
l[T.cid].m=[{
id:w}
];
var o=appCtxt.getById(w);
if(o){
o.cid=T.cid
}}}}
if(!x){
return u
}
if(h.length){
u.deleted.id=h.join(",")
}else{
delete u.deleted
}
for(var w in f){
var y=f[w];
var b=l[y.cid];
if(b&&b._wasVirtConv){
y._convCreateNode=b
}}
var v=[];
for(var r in q){
var k=l[r];
k.id=q[r];
k._newId=r;
v.push(k)
}
